import React, { useEffect, useState } from "react";

import axios from "axios";

import { Form } from "@unform/web";
import Input from "../../components/input";
import Select from "../../components/select";

import { Container } from "./styles";

export default function Filter({
  selectedLocale,
  setSelectedLocale,
  selectedCountry,
  setSelectedCountry,
}) {
  const [locale, setLocale] = useState({
    name: "",
    values: [],
  });

  const [country, setCountry] = useState({
    name: "",
    values: [],
  });

  useEffect(() => {
    axios
      .get("http://www.mocky.io/v2/5a25fade2e0000213aa90776")
      .then((response) => {
        console.log(response.data.filters);
        setLocale({
          name: response.data.filters[0].name,
          values: response.data.filters[0].values,
        });

        setCountry({
          name: response.data.filters[1].name,
          values: response.data.filters[1].values,
        });
      });
  }, []);

  return (
    <Container>
      <h2>Filtre suas músicas preferidas!</h2>
      <Form>
        <ul>
          <li>
            <Input
              name="name"
              type="text"
              label="Insira o nome da sua música"
            />
          </li>
          <Select
            name="Conty"
            locale={country}
            value={selectedCountry}
            onChange={(e) => setSelectedCountry(e.target.value)}
          />
          <li>
            <Select
              name="Conty"
              locale={locale}
              value={selectedLocale}
              onChange={(e) => setSelectedLocale(e.target.value)}
            />
          </li>
          <li></li>
        </ul>
      </Form>
    </Container>
  );
}
