import styled from "styled-components";

export const Container = styled.div`
  margin: 10px 0 0 40px;

  h1 {
    font-family: "Roboto", sans-serif;
    font-weight: 500;
  }

  section {
    display: flex;
    flex-direction: wrap;
    flex-wrap: wrap;

    div {
      padding: 20px;
      background: #282828;
      border-radius: 6px;
      margin: 10px;

      img {
        width: 160px;
        height: 170px;
      }

      h4 {
        font-family: "Roboto", sans-serif;
        font-weight: 600;
        color: #333;
        padding: 10px 1px;
        max-width: 160px;
      }

      p {
        max-width: 120px;
        margin-bottom: 0;
        font-size: 13px;
        color: #777;
      }
    }
  }

  #pagination {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 20px 0;
  }
`;
