import React, { useState, useEffect } from "react";
import { useLocation, Redirect } from "react-router-dom";
import { toast } from "react-toastify";

import axios from "axios";
import history from "../../services/history";

import { Container } from "./styles";

function Dashboard() {
  const [token, setToken] = useState("");
  const { hash } = useLocation();
  const parametro = useLocation();
  console.log("location", parametro);

  useEffect(() => {
    const firstReplace = hash.replace("#access_token=", "");
    const secondReplace = firstReplace.replace(
      "&token_type=Bearer&expires_in=3600&state=123",
      ""
    );
    setToken(secondReplace);
  }, []);

  useEffect(() => {
    axios
      .get(
        "https://api.spotify.com/v1/browse/featured-playlists?country=SE&timestamp=2020-06-25T11%3A54%3A32&offset=0&limit=2",
        {
          headers: {
            Authorization: "Bearer " + token,
          },
        }
      )
      .then((response) => console.log("playlist", response))
      .catch((err) => {
        toast.error(err.response.data.error.message);
      });
  }, []);

  return <Container>deu</Container>;
}

export default Dashboard;
