import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { toast } from "react-toastify";
import { FaSpotify } from "react-icons/fa";

import axios from "axios";
import Playlist from "../Playlist/index";
import Filter from "../Filter/index";

import { Container, Wrapper } from "./styles";

function Dashboard() {
  const { hash } = useLocation();
  const [playlists, setPlaylists] = useState({});

  useEffect(() => {
    const firstReplace = hash.replace("#access_token=", "");
    const secondReplace = firstReplace.replace(
      "&token_type=Bearer&expires_in=3600&state=123",
      ""
    );

    axios
      .get(
        "https://api.spotify.com/v1/browse/featured-playlists?country=BR&limit=30",
        {
          headers: {
            Authorization: "Bearer " + secondReplace,
          },
        }
      )
      .then((response) => setPlaylists(response.data))
      .catch((err) => {
        toast.error(err.response.data.error.message);
      });
  }, []);

  return (
    <Wrapper>
      <header>
        <FaSpotify />
        <h2>Spotifood</h2>
      </header>
      <Container>
        <nav>
          <Filter />
        </nav>
        <section>
          <Playlist playlists={playlists} />
        </section>
      </Container>
    </Wrapper>
  );
}

export default Dashboard;
