import React, { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { toast } from "react-toastify";

import axios from "axios";
import Playlist from "../Playlist/index";
import Filter from "../Filter/index";

import { Container } from "./styles";

function Dashboard() {
  const { hash } = useLocation();

  useEffect(() => {
    const firstReplace = hash.replace("#access_token=", "");
    const secondReplace = firstReplace.replace(
      "&token_type=Bearer&expires_in=3600&state=123",
      ""
    );

    axios
      .get(
        "https://api.spotify.com/v1/browse/featured-playlists?country=SE&limit=7",
        {
          headers: {
            Authorization: "Bearer " + secondReplace,
          },
        }
      )
      .then((response) => console.log("playlist", response))
      .catch((err) => {
        toast.error(err.response.data.error.message);
      });
  }, []);

  return (
    <Container>
      <Filter />
      <Playlist />
    </Container>
  );
}

export default Dashboard;
