import styled from "styled-components";

export const Container = styled.div`
  text-align: center;

  min-width: 250px;
  max-width: 250px;
  height: 91.4vh;
  border: 1px solid #dedede;

  h2 {
    padding: 20px 0 50px 0;
    font-family: "Roboto", sans-serif;
    text-overflow: ellipsis;
    overflow: hidden;
    max-width: 100%;
    display: inline-block;
    color: #fff;
  }

  form {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

    ul {
      li {
        list-style: none;

        .name {
          border-radius: 5px;
          background: #fff;

          label {
            color: #777;
          }
        }

        .country {
          width: 230px;
          background: #fff;
        }

        .locale {
          width: 230px;
          background: #fff;
        }

        .date {
          label {
            padding: 7px 10px;
          }

          background: #fff;
          padding: 10px 20px;
          width: 230px;
        }
      }
    }
  }
`;
