import styled from "styled-components";

export const Container = styled.div`
  margin: 100px 0 0 40px;

  section {
    display: flex;
    flex-direction: wrap;
    flex-wrap: wrap;

    div {
      img {
        margin-top: 20px;
        width: 120px;
        height: 100px;
        margin-right: 20px;
      }

      p {
        max-width: 120px;
        margin-bottom: 0;
      }
    }
  }
`;
