import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { toast } from "react-toastify";
import { FaSpotify } from "react-icons/fa";

import axios from "axios";
import Playlist from "../Playlist/index";
import Filter from "../Filter/index";

import { Container, Wrapper } from "./styles";

function Dashboard() {
  const { hash } = useLocation();
  const [playlists, setPlaylists] = useState({});

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const [selectedLocale, setSelectedLocale] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("");

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  useEffect(() => {
    const firstReplace = hash.replace("#access_token=", "");
    const secondReplace = firstReplace.replace(
      "&token_type=Bearer&expires_in=3600&state=123",
      ""
    );

    axios
      .get(
        `https://api.spotify.com/v1/browse/featured-playlists?country=BR&locale=${selectedLocale}&timestamp=2014-10-23T15%3A00%3A00&limit=${rowsPerPage}`,
        {
          headers: {
            Authorization: "Bearer " + secondReplace,
          },
        }
      )
      .then((response) => setPlaylists(response.data))
      .catch((err) => {
        toast.error(err.response.data.error.message);
      });
  }, [hash, rowsPerPage, selectedLocale]);

  return (
    <Wrapper>
      <header>
        <FaSpotify />
        <h2>Spotifood</h2>
      </header>
      <Container>
        <nav>
          <Filter
            selectedLocale={selectedLocale}
            setSelectedLocale={setSelectedLocale}
            selectedCountry={selectedCountry}
            setSelectedCountry={setSelectedCountry}
          />
        </nav>
        <section>
          <Playlist
            page={page}
            rowsPerPage={rowsPerPage}
            onChangePage={handleChangePage}
            onChangeRowPerPage={handleChangeRowsPerPage}
            playlists={playlists}
          />
        </section>
      </Container>
    </Wrapper>
  );
}

export default Dashboard;
