import React, { useEffect, useState } from "react";

import axios from "axios";

import { Form } from "@unform/web";
import Input from "../../components/input";
import Select from "../../components/select";
import { TextField } from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";
import { Container } from "./styles";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
    marginTop: 10
  },
}));

export default function Filter({
  selectedLocale,
  setSelectedLocale,
  selectedCountry,
  setSelectedCountry,
}) {
  const classes = useStyles();

  const [locale, setLocale] = useState({
    name: "",
    values: [],
  });

  const [country, setCountry] = useState({
    name: "",
    values: [],
  });

  useEffect(() => {
    axios
      .get("http://www.mocky.io/v2/5a25fade2e0000213aa90776")
      .then((response) => {
        console.log(response.data.filters);
        setLocale({
          name: response.data.filters[0].name,
          values: response.data.filters[0].values,
        });

        setCountry({
          name: response.data.filters[1].name,
          values: response.data.filters[1].values,
        });
      });
  }, []);

  return (
    <Container>
      <h2>Filtre suas músicas preferidas!</h2>
      <Form>
        <ul>
          <li>
            <Input
              name="name"
              type="text"
              label="Insira o nome da sua música"
            />
          </li>
          <li>
            <Select
              className="country"
              name="country"
              locale={country}
              value={selectedCountry}
              onChange={(e) => setSelectedCountry(e.target.value)}
            />
          </li>
          <li>
            <Select
              className="locale"
              name="locale"
              locale={locale}
              value={selectedLocale}
              onChange={(e) => setSelectedLocale(e.target.value)}
            />
          </li>
          <li>
            <TextField
              id="datetime-local"
              label="Data e Horário"
              type="datetime-local"
              defaultValue="2017-05-24T10:30"
              className={classes.textField}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </li>
        </ul>
      </Form>
    </Container>
  );
}
