import styled from "styled-components";

export const Container = styled.div`
  text-align: center;

  min-width: 250px;
  height: 91.4vh;
  border: 1px solid #dedede;

  h2 {
    padding: 20px 0 50px 0;
    font-family: "Roboto", sans-serif;
    font-weight: 500;
  }

  form {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

    ul {
      li {
        list-style: none;

        .country {
          width: 230px;
        }
        .locale {
          width: 185px;
        }
      }
    }
  }
`;
